mapper.select:
SELECT 

FROM cp_staff_result_score a

WHERE a.id = #{id}

SELECT 

FROM cp_staff_result_score a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cp_staff_result_score a


	a.del_flag = #{DEL_FLAG_NORMAL} 

		AND a.status = #{status}


		AND a.staff_id = #{staffId}


		AND a.job_level_id = #{jobLevelId}


		AND a.rater_level = #{raterLevel} 




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cp_staff_result_score SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
