mapper.select:
SELECT 

FROM cst_dict_para a

WHERE a.id = #{id}

SELECT 

FROM cst_dict_para a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.para_type = #{paraType}


		AND a.para_name = #{paraName}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM cst_dict_para a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE cst_dict_para SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
